import React from 'react'
import {View,TouchableOpacity,Text,Image,ImageBackground, FlatList} from 'react-native'
import info from './info'
import ImageContainer from './example'


const Hotels = ()=> {
    const ByLocation = ()=>{
        Pretoria=[{
            id:'0',title:'Maria'
        },
        {
            id:'1',title:'Fenyane'
        },
        {
            id:'2',title:'Monicca'
        },{
            id:'3',title:'Dennis'
        }
    ]
    }
   
    return(
        <>
        <View>
            <FlatList
            data={
                info.hotels
            }
            renderItem={({items})=>
            
            <ImageContainer key={items} hotels={items}/>
            }
            keyExtractor={item=>item.id}
            horizontal={true}
            />
        </View>
        </>
    )
}
export default Hotels
