import React,{useState} from 'react';
import {View,Text,Button,SafeAreaView,TextInput} from 'react-native'

const Landing = ({navigation})=>{

    const [users,setUsers] = useState([])
    const [location,setLocation] = useState()
    const [ rooms,setRooms] = useState('')
    const [ guests,setGuests] = useState('')

    const AddGuests = ({location,rooms,guests,item,index})=>{
        //  const key = {index}
        setUsers([...users,{
            id:users.length,
            location:location,
            rooms:rooms,
            guests:guests
        }])
        
    }
    const Display = ({index,item})=>{
        return(
            <View key={index}>
                <Text>Location</Text>
                <Text>{item.location}</Text>
                <Text>Location</Text>
                <Text>{item.location}</Text>
                <Text>{item.location}</Text>
            </View>
        )
    }
    return(
        <>
            <View>
                <Text>
                    Enter Details
                </Text>
                <TextInput placeholder='Enter Location' value={location} onChangeText={(text)=>setLocation(text)}/>
                <TextInput placeholder='Enter Rooms' value={rooms} onChangeText={(text)=>setRooms(text)}/>
                <TextInput placeholder='Enter Location' value={guests} onChangeText={(text)=>setGuests(text)}/>
                <Button title='Add Guest' onPress={AddGuests} />
            </View>

        </>
    )
}
export default Landing
