import firebase from './firebase'

const db = firebase.ref('/client')
const auth = firebase.app.auth()

class Client{
     signUp(email,password,) {
        return firebase.app.auth().then(res => {
            res.user.sendEmailVerification().then( action => {
                console.log('Verification email sent')
                firebase.ref(`/client`).child(res.user.uid).set({
                    email: email,
                    password:password,
                    uid: res.user.uid
                })
            }).catch( err => {
                console.log(err)
            })
        }).catch(err => {
            console.log(err.message)
        })
    }
    signIn(email,password,navigation){
        firebase.app.auth().signInWithEmailAndPassword(email,password).then(res=>{
            if(res.user.emailVerified){
                console.log('email verified')
                navigation.navigate('/bottomTab')
                localStorage.setItem('userid',res.user.uid)
                console.log('userid',res.user.uid)
            }else {
                console.log('Please verify your email address ')
                res.user.sendEmailVerification().then(res=>{
                    console.log('we send an email again, please verify your email')
                }).catch(err =>{
                    console.log(err.message)
                })
            }
        })
    }
    resetPassword(email){
        auth.sendPasswordResetEmail(email).then(()=>{
            console.log('password reset')
        }).catch(err=>{
            console.log(err.message)
        })
    }
    getLoggedData(id){
        return firebase.ref(`/user/${id}`)
    }
    logOut(navigation){
        firebase.app.auth().signOut().then(() => {
            console.log('logged out')
            localStorage.removeItem('userid')
            navigation.goBack()
        }).catch(err => {
            console.log(err.message)
        })
    }
}
export default new Client()
