import React from 'react';
import {View,Text,} from 'react-native'
import MapView,{Marker,Callout} from 'react-native-maps'

const Maps = ()=>{
    return(
        <>
            <MapView
                initialRegion={
                    {
                        latitude:42.882004,
                        longitude:74.582748,
                        latitudeDelta:0.0922,
                        longitudeDelta:0.0421
                    }
                }
                showsUserLocation={true}
                style={{flex:1}}
           >
                 <Marker 
                    coordinate={{
                        latitude:-23.907364,
                        longitude:29.461061,
                    }}
                    identifier='Hotel'
                >
                    <Callout tooltip={true} >

                    </Callout>
                </Marker>
            </MapView>
        </>
    )
}

export default Maps
