import React from 'react';
import {View,Text,ScrollView,ImageBackground,Image,StyleSheet} from 'react-native'
import info from './info';

const ImageContainer = ()=>{
    return(
        <View >
            {
                info.hotels.map(data=>
                    <View style={Styles.container}>
                        <ImageBackground source={data.image} style={{width:200,height:200,r}}>
                            <View style={Styles.textContainer} >
                                <Text style={Styles.hotelName}>{data.hotelname}</Text>
                                <Text style={Styles.reviews}>{data.review}</Text>
                            </View>
                        </ImageBackground>
                        
                    </View>
                    )
            }

        </View>
    )
}
const Styles = StyleSheet.create({
    container:{
        backgroundColor:'grey',
        padding:'2%'
    },
    textContainer:{
        backgroundColor:'white',
        margin:'auto',
    //    marginTop:'44%',
        width:'70%',
        padding:'1%',
        textAlign:'center',
        borderRadius:20
    },
    hotelName:{
        color: '#B2B2B2',
        fontSize:17,
        fontWeight:'600'
    },
    reviews:{
       color: '#FAA455'
    }
})
    


export default ImageContainer
