import React from 'react'
import {View,TouchableOpacity,Text,Image,ImageBackground, FlatList, SafeAreaView,StyleSheet,ScrollView} from 'react-native'
import info from './info'
import { Icon } from 'react-native-elements'
import ImageContainer from './example'


const Hotels = ()=> {
    return(
        <>
       <View style={styles.container}>
           <View style={styles.header}>
                <Icon name={'arrow-back'} color={'#1C5248'} style={{fontWeight:'700',marginTop:'14%'}} />
                <Text style={styles.textHead}>View Hotels By Location</Text>
           </View>
            <Text></Text>
         <ScrollView horizontal style={styles.imageContainer}>
         {
                info.hotels.map(data=>
                    <View style={styles.images}>
                        <ImageBackground source={data.image} style={{width:200,height:200,}} key={data.id}>
                            <View style={styles.textContainer} >
                                <Text style={styles.hotelName}>{data.hotelname}</Text>
                                <Text style={styles.reviews}>{data.review}</Text>
                            </View>
                        </ImageBackground>
                        
                    </View>
                    )
            }
           </ScrollView>
        
       </View>
      
    
        </>
    )
}
const styles= StyleSheet.create({
    container:{
        marginTop:'10%'
    },
    header:{
        display:'flex',
        flexDirection:'row',
        padding:'2%'
    },
    textContainer:{
        backgroundColor:'white',
        margin:'auto',
       marginTop:'44%',
        width:'70%',
        paddingLeft:'3%',
        justifyContent:'center',
        alignSelf:'center',
        borderRadius:20,
        textAlign:'center'
        ,height:'40%'
    },
    hotelName:{
        color: '#B2B2B2',
        fontSize:17,
        fontWeight:'600'
    },
    reviews:{
       color: '#FAA455'
    },
    images:{
        padding:'1%'
    },
    imageContainer:{
        backgroundColor:'red',
        marginTop:'3%'
    },
    textHead:{
        color:'#1C5248',
        fontSize:24,
        paddingLeft:'3%'
    }
})
export default Hotels
