import React,{useState} from 'react';
import {View,Text,Button,SafeAreaView,TextInput, FlatList} from 'react-native'

const Landing = ({navigation})=>{

    const [users,setUsers] = useState([])
    const [location,setLocation] = useState()
    const [ rooms,setRooms] = useState('')
    const [ guests,setGuests] = useState('')

    const AddGuests = ()=>{
        setUsers([...users,{
            id:users.length,
            location:location,
            rooms:rooms,
            guests:guests
        }])
        
    }
    const Display = ({index,item})=>{
        return(
            <View key={index}>
                <Text>Location</Text>
                <Text>{item.location}</Text>
                <Text>Rooms</Text>
                <Text>{item.rooms}</Text>
                <Text>Guests</Text>
                <Text>{item.guests}</Text>
                <Button title='Next' onPress={()=>navigation.navigate('about',{
                    itemId:item.key
                })}/>
            </View>
        )
    }
    {item.location}
    return(
        <>
            <View>
                <Text>
                    Enter Details
                </Text>
                <TextInput placeholder='Enter Location' value={location} onChangeText={(text)=>setLocation(text)}/>
                <TextInput placeholder='Enter Rooms' value={rooms} onChangeText={(text)=>setRooms(text)}/>
                <TextInput placeholder='Enter Location' value={guests} onChangeText={(text)=>setGuests(text)}/>
                <Button title='Add Guest' onPress={AddGuests} />
                {
                    users && users.length ? (
                        <FlatList
                        data={users}
                        renderItem={Display}
                        keyExtractor={item =>item.key}
                        />
                    ):(null)
                }
            </View>

        </>
    )
}
export default Landing
