import React from 'react'
import {View,TouchableOpacity,Text,Image,ImageBackground, FlatList} from 'react-native'
import info from './info'
import ImageContainer from './example'


const Hotels = ()=> {
    return(
        <>
        <View>
            <FlatList
            data={info.hotels}
            renderItem={({item})=>{
                return <ImageContainer key={item.id} />
                
            }}
            horizontal={true}
            ItemSeparatorComponent={()=>{
                return(
                    <View style={{
                        height:1,
                        width:Screen,
                        backgroundColor:'black'
                    }}
                    ></View>
                )
            }}
            />
        </View>
        </>
    )
}
export default Hotels
