import React from 'react'
import {View,TouchableOpacity,Text,Image,ImageBackground, FlatList} from 'react-native'
import info from './info'
import ImageContainer from './example'


const Hotels = ({hotelname})=> {
    
//    const NewItem =({data})=>{
//        return(

//        )
//    }
    return(
        <>
        <View>
            <FlatList
            data={info.hotels}
            renderItem={({item})=>{
                return <ImageContainer key={it} data={item}/>
                
            }}
            ItemSeparatorComponent={()=>{
                return(
                    <View style={{
                        height:1,
                        width:Screen,
                        backgroundColor:'black'
                    }}
                    ></View>
                )
            }}
            />
        </View>
        </>
    )
}
export default Hotels
