import React from 'react';
import {View,Text,} from 'react-native'
import MapView,{Marker,Callout} from 'react-native-maps'
import CallOut from './box';

const Maps = ({data})=>{
    return(
        <>
            <MapView
                initialRegion={
                    {
                        latitude:-24.2,
                        longitude:29.5,
                        latitudeDelta:0.005,
                        longitudeDelta:0.005
                    }
                }
                showsUserLocation={true}
                followsUserLocation={true}
                showsCompass={true}
                style={{flex:1}}
           >
                 <Marker 
                    coordinate={{
                        latitude:-26.061800,
                        longitude:28.03900,
                    }}
                    identifier='Hotel'
                >
                    <Callout tooltip={true} >
                        <CallOut data={data}/>
                    </Callout>
                </Marker>
            </MapView>
        </>
    )
}

export default Maps
